sys_script_client.script:
function onSubmit() {
	//Type appropriate comment here, and begin script below
	if (g_user.hasRoleExactly('x_1639982_intervie.hr')) {
		var flg = true;
		// clean the message and the red color
		g_form.clearMessages();
		g_form.getControl('u_glide_date_1').style.backgroundColor = '';
		g_form.getControl('states').style.backgroundColor = '';

		// interviewer check
		if (g_form.getValue('interviewer') == '') {
			g_form.addErrorMessage('Please choose the interviewer!');
			flg = false;
		}
		// interview date check
		var currDate = g_form.getValue('u_glide_date_1');
		if (currDate == '' && g_form.getValue('states') != 'not begain') {
			g_form.addErrorMessage('Please choose the interview date!');
			g_form.getControl('u_glide_date_1').style.backgroundColor = 'red';
			flg = false;
		}

		// states check
		if ( g_form.getValue('states') == 'not begain' 
			&& g_form.getValue('u_glide_date_1') != '') {
			g_form.addErrorMessage('Please choose the status!');
			g_form.getControl('states').style.backgroundColor = 'red';
			flg = false;
		}

		return flg;
   }
}
